import React, { useEffect, useState } from 'react';
import { Plane, MapPin, CreditCard, Calendar, Users } from 'lucide-react';
import { Alert, AlertDescription } from '@/components/ui/alert';

const Bookings = () => {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  
  const userId = localStorage.getItem('userId');

  const fetchBookings = async () => {
    try {
      setLoading(true);
      const response = await fetch('http://localhost:6001/fetch-bookings');
      const data = await response.json();
      setBookings(data.reverse());
      setError(null);
    } catch (error) {
      setError('Unable to fetch your bookings. Please try again later.');
      console.error('Error fetching bookings:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchBookings();
  }, []);

  const cancelTicket = async (id) => {
    try {
      const response = await fetch(`http://localhost:6001/cancel-ticket/${id}`, {
        method: 'PUT'
      });
      
      if (!response.ok) throw new Error('Failed to cancel ticket');
      
      await fetchBookings();
      alert(`Ticket cancelled successfully!`);
    } catch (error) {
      alert('Failed to cancel ticket. Please try again.');
      console.error('Error cancelling ticket:', error);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  const userBookings = bookings.filter((booking) => booking.user === userId);

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500" />
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-4">
        <Alert variant="destructive">
          <AlertDescription>{error}</AlertDescription>
        </Alert>
      </div>
    );
  }

  return (
    <div className="p-6 max-w-7xl mx-auto">
      <h1 className="text-3xl font-bold text-center mb-8">Your Bookings</h1>

      <div className="space-y-6">
        {userBookings.map((booking) => (
          <div key={booking._id} className="bg-white rounded-lg shadow-lg overflow-hidden">
            {/* Ticket Header with Flight Info */}
            <div className="bg-blue-500 p-4 text-white">
              <div className="flex items-center justify-between">
                <span className="text-lg font-semibold">{booking.flightName}</span>
                <span className="text-sm">#{booking.flightId}</span>
              </div>
            </div>

            {/* Cities and Route */}
            <div className="p-6 border-b border-gray-200">
              <div className="flex items-center justify-center space-x-8">
                <div className="text-center">
                  <MapPin className="w-6 h-6 mx-auto mb-2 text-gray-500" />
                  <p className="font-semibold">{booking.fromCity}</p>
                  <p className="text-sm text-gray-500">Departure</p>
                </div>
                
                <div className="flex-1 flex items-center justify-center">
                  <div className="w-full flex items-center">
                    <div className="h-0.5 flex-1 bg-gray-300"></div>
                    <Plane className="w-6 h-6 mx-4 text-blue-500 transform rotate-90" />
                    <div className="h-0.5 flex-1 bg-gray-300"></div>
                  </div>
                </div>

                <div className="text-center">
                  <MapPin className="w-6 h-6 mx-auto mb-2 text-gray-500" />
                  <p className="font-semibold">{booking.toCity}</p>
                  <p className="text-sm text-gray-500">Arrival</p>
                </div>
              </div>
            </div>

            {/* Ticket Details */}
            <div className="p-6 grid grid-cols-2 md:grid-cols-4 gap-4">
              <div>
                <div className="flex items-center text-gray-600 mb-1">
                  <Calendar className="w-4 h-4 mr-2" />
                  <span className="text-sm">Date</span>
                </div>
                <p className="font-semibold">{formatDate(booking.journeyDate)}</p>
              </div>

              <div>
                <div className="flex items-center text-gray-600 mb-1">
                  <Users className="w-4 h-4 mr-2" />
                  <span className="text-sm">Passengers</span>
                </div>
                <p className="font-semibold">{booking.passengers.length}</p>
              </div>

              <div>
                <div className="flex items-center text-gray-600 mb-1">
                  <CreditCard className="w-4 h-4 mr-2" />
                  <span className="text-sm">Price</span>
                </div>
                <p className="font-semibold">₹{booking.totalPrice}</p>
              </div>

              <div>
                <div className="flex items-center text-gray-600 mb-1">
                  <span className="w-4 h-4 mr-2">🪑</span>
                  <span className="text-sm">Seats</span>
                </div>
                <p className="font-semibold">
                  {booking.passengers.map(passenger => passenger.seatNumber).join(', ')}
                </p>
              </div>
            </div>

            {/* Class and Status */}
            <div className="px-6 pb-6 flex items-center justify-between">
              <div>
                <span className="inline-block px-3 py-1 bg-blue-100 text-blue-800 rounded-full text-sm">
                  {booking.seatClass}
                </span>
              </div>

              {booking.bookingStatus === "confirmed" && (
                <button
                  onClick={() => cancelTicket(booking._id)}
                  className="px-4 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600 transition-colors"
                >
                  Cancel Ticket
                </button>
              )}
            </div>
          </div>
        ))}

        {userBookings.length === 0 && (
          <div className="text-center p-8 bg-gray-50 rounded-lg">
            <p className="text-gray-500">No bookings found</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Bookings;
