// Bookings.jsx
import React, { useEffect, useState } from 'react';
import './Booking';

const Bookings = () => {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  
  const userId = localStorage.getItem('userId');

  const fetchBookings = async () => {
    try {
      setLoading(true);
      const response = await fetch('http://localhost:6001/fetch-bookings');
      const data = await response.json();
      setBookings(data.reverse());
      setError(null);
    } catch (error) {
      setError('Unable to fetch your bookings. Please try again later.');
      console.error('Error fetching bookings:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchBookings();
  }, []);

  const cancelTicket = async (id) => {
    try {
      const response = await fetch(`http://localhost:6001/cancel-ticket/${id}`, {
        method: 'PUT'
      });
      
      if (!response.ok) throw new Error('Failed to cancel ticket');
      
      await fetchBookings();
      alert(`Ticket cancelled successfully!`);
    } catch (error) {
      alert('Failed to cancel ticket. Please try again.');
      console.error('Error cancelling ticket:', error);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  const userBookings = bookings.filter((booking) => booking.user === userId);

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loader"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-message">
        <span className="error-icon">⚠️</span>
        <p>{error}</p>
      </div>
    );
  }

  return (
    <div className="bookings-page">
      <h1>Your Bookings</h1>

      <div className="bookings-container">
        {userBookings.map((booking) => (
          <div key={booking._id} className="ticket">
            {/* Ticket Header */}
            <div className="ticket-header">
              <span className="airline-name">{booking.flightName}</span>
              <span className="flight-number">#{booking.flightId}</span>
            </div>

            {/* Cities and Route */}
            <div className="route-section">
              <div className="city-info departure">
                <span className="city-pin">📍</span>
                <p className="city-name">{booking.fromCity}</p>
                <p className="city-label">Departure</p>
              </div>
              
              <div className="route-line">
                <span className="plane-icon">✈️</span>
              </div>

              <div className="city-info arrival">
                <span className="city-pin">📍</span>
                <p className="city-name">{booking.toCity}</p>
                <p className="city-label">Arrival</p>
              </div>
            </div>

            {/* Ticket Details */}
            <div className="ticket-details">
              <div className="detail-item">
                <div className="detail-label">
                  <span>📅</span>
                  <span>Date</span>
                </div>
                <p>{formatDate(booking.journeyDate)}</p>
              </div>

              <div className="detail-item">
                <div className="detail-label">
                  <span>👥</span>
                  <span>Passengers</span>
                </div>
                <p>{booking.passengers.length}</p>
              </div>

              <div className="detail-item">
                <div className="detail-label">
                  <span>💳</span>
                  <span>Price</span>
                </div>
                <p>₹{booking.totalPrice}</p>
              </div>

              <div className="detail-item">
                <div className="detail-label">
                  <span>💺</span>
                  <span>Seats</span>
                </div>
                <p>{booking.passengers.map(passenger => passenger.seatNumber).join(', ')}</p>
              </div>
            </div>

            {/* Class and Cancel Button */}
            <div className="ticket-footer">
              <span className="seat-class">{booking.seatClass}</span>
              {booking.bookingStatus === "confirmed" && (
                <button
                  onClick={() => cancelTicket(booking._id)}
                  className="cancel-button"
                >
                  Cancel Ticket
                </button>
              )}
            </div>
          </div>
        ))}

        {userBookings.length === 0 && (
          <div className="no-bookings">
            <p>No bookings found</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Bookings;
